mapper.select:
/* selectWorkList */ SELECT PWI.IDX, PWI.PROJECT_IDX, PI.NAME AS PROJECT_NAME, PWI.WORKFLOW, CASE WHEN PWI.WORKFLOW IS NULL THEN '' ELSE (SELECT VALUE FROM CODE_INFO WHERE USE_YN = 'Y' AND GRP_CODE = 'WORK_WORKFLOW' AND CODE = PWI.WORKFLOW) END AS WORKFLOW_NAME, DATE_FORMAT(WORK_DATE_ST, '%Y-%m-%d') AS PLAN_DATE_ST, DATE_FORMAT(WORK_DATE_ED, '%Y-%m-%d') AS PLAN_DATE_ED, PWI.TARGET_USER_IDX, CASE WHEN PWI.TARGET_USER_IDX IS NULL THEN '' ELSE (SELECT NAME FROM USER_INFO WHERE USE_YN = 'Y' AND IDX = PWI.TARGET_USER_IDX) END AS TARGET_USER_NAME, PWI.TITLE, PWI.CONTENT FROM PROJECT_WORK_INFO PWI INNER JOIN PROJECT_INFO PI ON PI.IDX = PWI.PROJECT_IDX AND PI.USE_YN = 'Y' WHERE PWI.USE_YN = 'Y'
AND PWI.PROJECT_IDX = #{projectIdx}
ORDER BY PWI.IDX DESC
